sys_script_client.script:
function onLoad() {
	var ga = new GlideAjax('SnowDevelAJAX');
	ga.addParam('sysparm_name', 'getCollisionsByUpdateSet');
	ga.addParam('sysparm_updateSetId', g_form.getUniqueValue());
	ga.getXML(displayConflicts);

	function displayConflicts(response) {
		var answer = response.responseXML.documentElement.getAttribute('answer');
		var confl = JSON.parse(answer);

		if(confl.conflicts.length == 0 && confl.issues.length == 0) {
			g_form.addInfoMessage('No conflicts found In Progress nor Complete');
		}
		else {
			if(confl.conflicts.length > 0) {
				g_form.addErrorMessage(constructMessage('Beware! There are later open update dates for these objects:', confl.conflicts));
			}

			if(confl.issues.length > 0) {
				g_form.addInfoMessage(constructMessage('Note that these objects are also in update sets not yet ignored nore released:', confl.issues));
			}
		}
	}

	var constructMessage = function(header, results) {
		var msg = header;
		for(i = 0; i < results.length; i++) {
			msg += '<li>' + results[i].name + ' IS IN <a target=_blank href=/nav_to.do?uri=%2Fsys_update_set.do%3Fsys_id%3D' + results[i].updateSetId + '>' + results[i].updateSet + '</a></li>';
		}

		return msg;
	};
}
